import unittest
import ORsudoku
  
class TestPuzzle(unittest.TestCase):
	# Puzzle: World Wide Whispers (partial)
	# Author: jeremydover
		
	def test_puzzle(self):
		p = ORsudoku.sudoku(3)
		p.setGlobalWhispers()
		p.setGivenArray([115,158,173,194,214,226,245,261,331,462,517,556,591,619,743,865,924])
		
		self.assertEqual(p.countSolutions(test=True),'8:','Failed World Wide Whispers (partial, 8 solutions)')
		
if __name__ == '__main__':
    unittest.main()